import React from 'react';
import { List, Image, Button, Transition, Grid } from 'semantic-ui-react';
import API from '../../../utils/API';
const moment = require('moment');

export default class NotificationsHistory extends React.Component{

    state = {
        notifications: [],
    };
    _mounted = false;
    componentDidMount = async() => {
        this._mounted = true;
        await API.getNotifications()
            .then(response => {
                if (response.status === 200) {
                    this._mounted && this.setState({notifications: response.data.notifications}, () => console.log(this.state.notifications));
                }
            })
            .catch(e => console.log(e));
    };

    componentWillUnmount() {
        this._mounted = false;
    }

    renderSwitchType(type) {
        switch (type) {
            case 1:
                return 'visited your profile  ';
            case 2:
                return 'send you a message  ';
            case 3:
                return 'liked you back  ';
            case 4:
                return 'remove his like  ';
        }
    };

    handleDismiss = (i) => {
        if (this._mounted) {
            let notifications = this.state.notifications;
            let removed = notifications.splice(i, 1);
            this.setState({notifications: notifications}, () => console.log(this.state.notifications));
        }
    };

    render(){
        return (
            <div>
                <Grid columns={1} textAlign={'center'}>
                    <Grid.Row >
                        <Transition.Group
                            as={List}
                            duration={200}
                            divided
                            size='huge'
                            verticalAlign='middle'
                        >
                            {this.state.notifications.map((obj, i) => (
                                <List.Item>
                                    <List.Content>
                                        <List.Content floated='right'>
                                            <Button
                                                onClick={() => this.handleDismiss(i)}>
                                                Dismiss
                                            </Button>
                                        </List.Content>
                                        <Image avatar src={obj.img_link}/>
                                        <List.Content as='a'>{obj.firstname} {' '} {i}</List.Content>
                                        <List.Description>
                                            {this.renderSwitchType(obj.type)}
                                            on {moment(obj.date).fromNow()}
                                        </List.Description>
                                    </List.Content>
                                </List.Item>
                            ))}
                        </Transition.Group>
                    </Grid.Row>
                </Grid>
            </div>
        )
    }
}
