import React from 'react';
import UserMiniCard from "../../components/UserMiniCard/UserMiniCard";
import API from './../../utils/API';
import InputRange from 'react-input-range';
import Interests from "../../components/EditProfil/AddInterests/Interests/Interests";
import { Header, Image, Modal, Dimmer, Loader, Grid, Rail, Segment, Divider, Checkbox, Portal} from "semantic-ui-react";
import 'react-input-range/lib/css/index.css';
import Button from "semantic-ui-react/dist/commonjs/elements/Button";
import _ from "lodash";

const DEFAULT_STATE = {
    showModal: false,
    userIdFocus: 0,
    userFocused: null,
    clear: false,
    users: [],
    warnings: [],
    loading: false,
    interests: [],
    distanceRange: 250,
    ageRange: {
        min: 19,
        max: 27,
    },
    popularityRange: {
        min: 0,
        max: 50,
    }
};
class Wall extends React.Component {

    state = {...DEFAULT_STATE};
    componentDidMount = async() => {

        this.setState({loading: true});
        // Check if user can view users
        await API.checkUserView()
            .then(res => { this.canView = res.status === 200 })
            .catch(() => { this.props.history.push('/') });
        if(!this.canView)
            this.props.history.push('/')
        await this.searchMatch();
        this.setState({loading: false});
    };

    // Filter
    handleChange = async(value, id) => {
        if (id === "distanceRange")
            this.setState({distanceRange: value});
        else if (id === "ageRange")
            this.setState({ageRange: value});
        else if (id === "popularityRange")
            this.setState({popularityRange: value});
    };

    // Search button handle
    searchMatch = async() => {
        this.setState({loading: true});
        const {distanceRange, ageRange, popularityRange } = this.state;
        let users = [];
        // Get users
        await API.getUsers(distanceRange, ageRange, popularityRange)
            .then(response => {
                if (response.status === 200)
                    users = response.data.users;
            })
            .catch(error => {
                if(typeof error.response !== 'undefined')
                    this.setState({warnings : error.response.data.warnings})
            });
        this.setState({users: users});
        this.setState({loading: false});

    };

    // Cliked user handle
    clickedUser = async(userIdFocus) => {
        this.setState({ userIdFocus: userIdFocus, showModal: true})
        await API.getUserIdProfile(userIdFocus)
            .then(res => {
                if (res.status === 200){
                    this.setState({userFocused: res.data.user});
                }
            })
    };
    handleClose = () => {
        this.setState({ userIdFocus: null, showModal: false})
    }
    render() {
        const { showModal, userIdFocus, users, loading, distanceRange, ageRange, popularityRange, interests } = this.state;
            return (
                <div className="WallContainer">
                    <Modal
                        open={showModal}
                        onClose={this.handleClose}>
                        <Modal.Header>Select a Photo</Modal.Header>
                        <Modal.Content image>
                            <Image wrapped size='medium' src='/images/avatar/large/rachel.png' />
                            <Modal.Description>
                                <Header>Default Profile Image</Header>
                                <p>
                                    We've found the following gravatar image associated with your e-mail
                                    address.
                                </p>
                                <p>Is it okay to use this photo?</p>
                            </Modal.Description>
                        </Modal.Content>
                    </Modal>
                    <Dimmer active={loading}>
                        <Loader size='massive'/>
                    </Dimmer>
                    <Grid centered stackable>
                        <Grid.Column floadted='left' textAlign='left' mobile={16} tablet={6} computer={4} >
                            <Segment inverted className="SegmentFilter">
                            <div className="filterContainer">
                                <Grid.Row>
                                    <Grid.Row>
                                        <div className="DistanceRangeContainer">
                                            <h1 className="DistanceRangeTitle"><strong>Distance</strong> <span className="RangeTitle"> 0 - {distanceRange} km</span></h1>
                                        </div>
                                    </Grid.Row>
                                    <Grid.Row>
                                        <InputRange
                                            draggableTrack
                                            maxValue={5000}
                                            minValue={0}
                                            onChange={value => this.handleChange(value, "distanceRange")}
                                            formatLabel={value => `${value} km`}
                                            value={distanceRange} />
                                    </Grid.Row>
                                </Grid.Row>
                                <Divider hidden/>
                                <Grid.Row>
                                    <Grid.Row>
                                        <div className="DistanceRangeContainer">
                                            <h1 className="DistanceRangeTitle"><strong>Age</strong> <span className="RangeTitle"> {ageRange.min} - {ageRange.max}</span></h1>
                                        </div>
                                    </Grid.Row>
                                    <Grid.Row>
                                        <InputRange
                                            draggableTrack
                                            maxValue={99}
                                            minValue={18}
                                            onChange={value => this.handleChange(value, "ageRange")}
                                            value={ageRange} />
                                    </Grid.Row>
                                </Grid.Row>
                                <Divider hidden/>
                                <Grid.Row>
                                    <Grid.Row>
                                        <div className="DistanceRangeContainer">
                                            <h1 className="DistanceRangeTitle"><strong>Popularity</strong> <span className="RangeTitle"> {popularityRange.min} - {popularityRange.max}</span></h1>
                                        </div>
                                    </Grid.Row>
                                    <Grid.Row>
                                        <InputRange
                                            draggableTrack
                                            maxValue={500}
                                            minValue={0}
                                            onChange={value => this.handleChange(value, "popularityRange")}
                                            value={popularityRange} />
                                    </Grid.Row>
                                </Grid.Row>
                                <Divider hidden/>
                                <Grid.Row>
                                    <Segment className="IncludeInterestsTitle">
                                        <Checkbox label='Include my interests' defaultChecked />
                                    </Segment>
                                </Grid.Row>
                                <Grid.Row centered>
                                    <Interests
                                        interests={interests}
                                        deleteInterest={this.deleteInterest} />
                                </Grid.Row>
                                <Divider hidden/>
                                <Grid.Row>
                                    <Button
                                        onClick={this.searchMatch}>
                                        Search
                                    </Button>
                                </Grid.Row>
                            </div>
                        </Segment>
                    </Grid.Column>
                            <Grid.Column mobile={16} tablet={8} computer={10}>
                                <Segment inverted>
                                </Segment>
                                        <Grid centered stackable  >
                                            <UserMiniCard
                                                users={users}
                                                clickedUser={this.clickedUser}
                                            />
                                        </Grid>
                            </Grid.Column>
                    </Grid>
                </div>
            );
    }
}


export default Wall;
