import React from 'react';
import {Grid, Image} from "semantic-ui-react";


const LoadImages = (props) => {
    if (Array.isArray(props.data) && props.data.length > 0) {
        return props.data.map((item, key) => {
            return (
                <Grid.Column key={key} mobile={8} tablet={6} computer={6}>
                        <Image
                            className="ImagePreview"
                            img_id={item.img_id}
                            src={"http://localhost:3000/" + item.img_link}
                            rounded
                            fluid
                            label={{ as: 'a', corner: 'right', icon: 'close' , onClick: (img_id) => {this.props.deleteImage(img_id)} }}
                        />
                </Grid.Column>
            )
        });
    }
    else {
        return null
    }
};

export default LoadImages;
