import React from 'react';
import UserMiniCard from "../../components/UserMiniCard/UserMiniCard";
import API from './../../utils/API';
import InputRange from 'react-input-range';
import InfiniteScroll from 'react-infinite-scroller';
import {Dimmer, Loader, Grid, Segment, Divider, Checkbox} from "semantic-ui-react";
import 'react-input-range/lib/css/index.css';
import Button from "semantic-ui-react/dist/commonjs/elements/Button";
import ModalUser from '../../components/ModalUser/ModalUser';

const DEFAULT_STATE = {
    loadingModal: '',
    nextUsers: '',
    hasMoreContent: false,
    userIdFocus: null,
    clear: false,
    users: [],
    warnings: [],
    loading: true,
    interests: [],
    distanceRange: 250,
    ageRange: {
        min: 19,
        max: 27,
    },
    popularityRange: {
        min: 0,
        max: 50,
    },
};
class Wall extends React.Component {

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this._mounted = false;
    }

    componentDidMount = async() => {
        this._mounted = true;
        await API.checkUserView()
            .then(res => { this.canView = res.status === 200 })
            .catch(() => { this.props.history.push('/') });
        if(!this.canView)
            this.props.history.push('/');
        await this.searchMatch();
        this._mounted && this.setState({loading: false});
    };

    componentWillUnmount() {
        this._mounted = false;
    }

    // Filter
    handleChange = async(value, id) => {
        if (id === "distanceRange")
            this._mounted && this.setState({distanceRange: value});
        else if (id === "ageRange")
            this._mounted && this.setState({ageRange: value});
        else if (id === "popularityRange")
            this._mounted && this.setState({popularityRange: value});
    };

    // Search button handle
    searchMatch = async() => {
        this._mounted && this.setState({loading: true});
        const {distanceRange, ageRange, popularityRange } = this.state;
        let users = [];
        await API.getUsers(distanceRange, ageRange, popularityRange)
            .then(response => {
                if (response.status === 200)
                    users = response.data.users;
            });
        let nextUsers = 0;
        if(users.length > 20)
           nextUsers = users.slice(21);
        this._mounted && this.setState({
            nextUsers: nextUsers,
            users: users.slice(0, 21),
            hasMoreContent: users.length > 20
        });
        this._mounted && this.setState({loading: false});

    };


    clickedUser = async(userIdFocus) => {
        // Send notification
        this.props.sWallVisit(userIdFocus);
        this._mounted && this.setState({ userIdFocus: userIdFocus, loadingMinCard: true});
    };

    handleCardUserComplete = () => this._mounted && this.setState({loadingMinCard: false});

    userLike = async(userIdFocus) => {
        await API.userLike(userIdFocus)
            .then(response => {
                if (response.status === 200 && typeof response.data.liked != 'undefined') {
                    this._mounted && this.setState({liked: response.data.liked});
                    // Socket
                    if (response.data.liked === true)
                        this.props.s_like(userIdFocus)
                }
            })
            .catch(err => console.log(err));
    };
    handleBottomPage = () => {
        const { nextUsers, users } = this.state;
        if (nextUsers.length){
            for (let i = 0; i < nextUsers.length && i <= 20; i++)
                users.push(nextUsers[i]);
            this._mounted && this.setState({users: users, nextUsers: nextUsers.slice(21)});
        }
        else
            this._mounted && this.setState({hasMoreContent: false});
    };
    render() {
        const { hasMoreContent, userIdFocus, liked, users, loadingMinCard, loading, distanceRange, ageRange, popularityRange } = this.state;
            return (
                <div className="WallContainer">
                    <ModalUser userid={userIdFocus} handleCardUserComplete={this.handleCardUserComplete} liked={liked} userLike={this.userLike} handleClose={this.handleClose} />
                    <Dimmer active={loading}>
                        <Loader size='massive'/>
                    </Dimmer>
                    <Grid centered columns={'equal'} stackable>
                        <Grid.Column mobile={16} tablet={6} computer={6} largeScreen={4} widescreen={3}>
                            <Segment inverted className="SegmentFilter">
                            <div className="filterContainer">
                                <Grid.Row>
                                    <Grid.Row>
                                        <div className="DistanceRangeContainer">
                                            <h1 className="DistanceRangeTitle"><strong>Distance</strong> <span className="RangeTitle"> 0 - {distanceRange} km</span></h1>
                                        </div>
                                    </Grid.Row>
                                    <Grid.Row>
                                        <InputRange
                                            draggableTrack
                                            maxValue={5000}
                                            minValue={0}
                                            onChange={value => this.handleChange(value, "distanceRange")}
                                            formatLabel={value => `${value} km`}
                                            value={distanceRange} />
                                    </Grid.Row>
                                </Grid.Row>
                                <Divider hidden/>
                                <Grid.Row>
                                    <Grid.Row>
                                        <div className="DistanceRangeContainer">
                                            <h1 className="DistanceRangeTitle"><strong>Age</strong> <span className="RangeTitle"> {ageRange.min} - {ageRange.max}</span></h1>
                                        </div>
                                    </Grid.Row>
                                    <Grid.Row>
                                        <InputRange
                                            draggableTrack
                                            maxValue={99}
                                            minValue={18}
                                            onChange={value => this.handleChange(value, "ageRange")}
                                            value={ageRange} />
                                    </Grid.Row>
                                </Grid.Row>
                                <Divider hidden/>
                                <Grid.Row>
                                    <Grid.Row>
                                        <div className="DistanceRangeContainer">
                                            <h1 className="DistanceRangeTitle"><strong>Popularity</strong> <span className="RangeTitle"> {popularityRange.min} - {popularityRange.max}</span></h1>
                                        </div>
                                    </Grid.Row>
                                    <Grid.Row>
                                        <InputRange
                                            draggableTrack
                                            maxValue={500}
                                            minValue={0}
                                            onChange={value => this.handleChange(value, "popularityRange")}
                                            value={popularityRange} />
                                    </Grid.Row>
                                </Grid.Row>
                                <Divider hidden />
                                <Divider />
                                <Grid.Row className="IncludeInterestsTitle">
                                        <Checkbox label='Include my interests' defaultChecked />
                                </Grid.Row>
                                <Divider />
                                <Grid.Row className="ButtonSearch">
                                    <Button
                                        id="search"
                                        size='huge'
                                        onClick={this.searchMatch}>
                                        Search
                                    </Button>
                                </Grid.Row>
                            </div>
                        </Segment>

                    </Grid.Column>
                            <Grid.Column>
                                <Grid stackable >
                                    <UserMiniCard
                                        userIdFocus={userIdFocus}
                                        loading={loadingMinCard}
                                        users={users}
                                        clickedUser={this.clickedUser}
                                    />
                                </Grid>
                            </Grid.Column>
                    </Grid>
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={this.handleBottomPage}
                        hasMore={hasMoreContent}
                    > </InfiniteScroll>
                </div>
            );
    }
}


export default Wall;
