import React from 'react';
import { Modal, Button } from 'semantic-ui-react';
const DEFAULT_STATE = {
    open: false,
}

class ModalUserActions extends React.Component{

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this._mounted = false;
    }

    componentDidMount() {
    }
    close = () => {this._mounted && this.setState({open: false})};

    render(){
        const {open} = this.state;
        return (
            <div>
                <Modal size='tiny' open={open} onClose={this.close}>
                    <Modal.Header>Report user</Modal.Header>
                    <Modal.Content>
                        <p>Are you sure you want to report this user ?</p>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button negative>No</Button>
                        <Button
                            positive
                            icon='checkmark'
                            labelPosition='right'
                            content='Yes'
                        />
                    </Modal.Actions>
                </Modal>
            </div>
        )
    }
}

export default ModalUserActions;
