import React from 'react';
import {Segment, Icon, Menu, Grid, Image, Button, Divider} from 'semantic-ui-react'
import { Redirect } from 'react-router-dom'
import { withRouter } from 'react-router';
class NavbarMenu extends React.Component {

    state = {
        activeItem: 'wall',
        redirect: null,
    }

    constructor(props){
        super(props);
    }
    handleItemClick = (e, { name }) => {
        this.setRedirect(name)
        this.setState({ activeItem: name })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to={this.state.redirect} />
        }
    }
    setRedirect = (name) => {
        this.setState({redirect: "/" + name})
    }
    render(){
        console.log(this.props);
        const { activeItem } = this.state
        return(
                <Menu inverted icon='labeled' stackable size={"massive"} className="Menu">
                    {this.renderRedirect()}
                    <Menu.Item className="Logo">
                        <Image
                            src='./../../img/MatchApp-Logo.png'
                            size={"small"}/>
                    </Menu.Item>
                    <Menu.Menu icon='labeled'>
                        <Menu.Item
                            name='wall'
                            active={activeItem === 'wall'}
                            onClick={this.handleItemClick} >
                            <Icon name='home' />
                            Wall
                        </Menu.Item>
                        <Menu.Item
                            name='messages'
                            active={activeItem === 'messages'}
                            onClick={this.handleItemClick}>
                            <Icon name='conversation' />
                            Messages
                        </Menu.Item>
                    </Menu.Menu>
                    <Menu.Item
                        name='notifications'
                        active={activeItem === 'notifications'}
                        onClick={this.handleItemClick}>
                        <Icon name='bell' />
                        Notifications
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        <Menu.Item
                            name='signup'
                            icon='signup'
                            active={activeItem === 'signup'}
                            onClick={this.handleItemClick} >
                            <Icon name='signup' />
                            Sign up
                        </Menu.Item>
                        <Menu.Item
                            name='login'
                            icon='login'
                            active={activeItem === 'login'}
                            onClick={this.handleItemClick} >
                            <Icon name='user' />
                            Log in
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>

        )
    }
}
export default withRouter(NavbarMenu)
