import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";
import Aux from './hoc/Aux';
import Signup from "./components/Signup/Signup.js";
import Wall from './containers/Wall/Wall';
import Login from "./components/Login/Login";
import EditProfile from "./components/EditProfil/EditProfil";
import withAuth from "./utils/withAuth";
import './App.scss';
import BootstrapMenu from './components/Menu/BootstrapMenu';
import Footer from './containers/Footer/Footer';
import Notifications from "./components/Notifications/Notifications";
import NotificationsHistory from "./components/Notifications/NotificationsHistory/NotificationsHistory";
import ForgotPassword from './components/ForgotPassword/ForgotPassword';
import ChangePassword from './components/ForgotPassword/ChangePassword';
import ChangeMyEmail from "./components/EditProfil/ChangeMyMail/ChangeMyMail";
import io from 'socket.io-client';


class App extends Component {

    constructor(props){
        super(props);
        this.state = { connected: false };
        this._mounted = false;
        this.socket = io('http://localhost:8000');
        this.s_wallvisit = this.s_wallvisit.bind(this);
        this.notifNb = React.createRef();
    }
    componentDidMount = async() => {
        this._mounted = true;
        this.socket.on("getnotif", () => this._mounted && this.setState((prevState) => ({ notifNb: prevState.notifNb + 1 })) );
    };

    getInnerRef = (ref) => { this.innerRef = ref; console.log(this.innerRef) };
    updateNotifNb = (notifNb) => {
        this.notifNb && this.notifNb.current.updateNotifNb(notifNb);
    };

    // Sockets
    s_wallvisit(userIdFocus){this.socket.emit("wall:visit", userIdFocus)};
    s_like = (userIdFocus) => {this.socket.emit("like", userIdFocus)};
    s_userlogin = () => {this.socket.emit("userlogin")};
    s_logout = () => {this.socket.emit("logout")};

    // Handle Connect
    isConnected = (bool) => {this.setState({connected: bool})};

    render() {
        const { notifNb } = this;
        const { connected } = this.state;
        return (
            <Aux>
                <BootstrapMenu isConnected={connected} ref={notifNb} handleConnected={this.isConnected} />
                <Notifications push/>
                <div className="content">
                    <Switch>
                        <Route exact path="/" component={withAuth(() => <Wall sWallVisit={this.s_wallvisit} s_like={this.s_like}/>)}/>
                        <Route exact path="/profile" component={withAuth(EditProfile)} />
                        <Route exact path="/signup" component={Signup} />
                        <Route exact path="/forgotpassword/:token" component={ChangePassword} />
                        <Route exact path="/forgotpassword" component={ForgotPassword} />
                        <Route exact path="/changepassword" component={ChangePassword} />
                        <Route exact path="/changemail" component={withAuth(ChangeMyEmail)} />
                        <Route exact path="/notifications" component={withAuth(() => <NotificationsHistory  UpdateNotifNb={this.updateNotifNb} sWallVisit={this.s_wallvisit} s_like={this.s_like}/>)}/>
                        <Route exact path="/login" render={(props) => <Login {...props} s_userlogin={this.s_userlogin} handleConnected={this.isConnected} />} />
                        <Route exact path={"/login/:token"} component={Login} />
                    </Switch>
                </div>
                <Footer />
            </Aux>
        );
    }
}
export default App;
