import React from 'react';
import {Segment, Icon, Menu, Grid, Image, Button, Divider} from 'semantic-ui-react'
import { Redirect } from 'react-router-dom'
import { withRouter } from 'react-router';
import API from '../../utils/API'
class NavbarMenu extends React.Component {

    state = {
        activeItem: null,
        redirect: null,
        connected: false,
    }

    constructor(props){
        super(props);
    }

    componentDidMount() {
        this.setState({activeItem: this.props.location.pathname})
        API.withAuth()
            .then(res => {
                if (res.status === 200) {
                    this.setState({connected: true});
                }
                else
                    throw new Error(res.error);
            })
            .catch(() => {this.setState({connected: false});});
    }

    handleItemClick = (e, { name }) => {
        this.setRedirect(name)
        this.setState({ activeItem: name })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to={this.state.redirect} />
        }
    }
    setRedirect = (name) => {
        this.setState({redirect: name})
    }

    isConnected = () => {
        const { activeItem } = this.state
        if (!this.state.connected)
            return (
                <div>
                <Menu.Item
                    name='/signup'
                    icon='signup'
                    active={activeItem === '/signup'}
                    onClick={this.handleItemClick} >
                    <Icon name='signup' />
                    Sign up
                </Menu.Item>
                <Menu.Item
                    name='/login'
                    icon='login'
                    active={activeItem === '/login'}
                    onClick={this.handleItemClick} >
                    <Icon name='user' />
                    Log in
                </Menu.Item>
                </div>
            )
        else {
            return <div>
                test
            </div>
        }
    }
    render(){
        const { activeItem } = this.state
        return(
                <Menu inverted icon='labeled' stackable size={"massive"} className="Menu">
                    {this.renderRedirect()}
                    <Menu.Item className="Logo">
                        <Image
                            src='./../../img/MatchApp-Logo.png'
                            size={"small"}/>
                    </Menu.Item>
                    <Menu.Menu icon='labeled'>
                        <Menu.Item
                            name='/wall'
                            active={activeItem === '/wall'}
                            onClick={this.handleItemClick} >
                            <Icon name='home' />
                            Wall
                        </Menu.Item>
                        <Menu.Item
                            name='/messages'
                            active={activeItem === '/messages'}
                            onClick={this.handleItemClick}>
                            <Icon name='conversation' />
                            Messages
                        </Menu.Item>
                    </Menu.Menu>
                    <Menu.Item
                        name='/notifications'
                        active={activeItem === '/notifications'}
                        onClick={this.handleItemClick}>
                        <Icon name='bell' />
                        Notifications
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        {this.isConnected()}
                    </Menu.Menu>
                </Menu>

        )
    }
}
export default withRouter(NavbarMenu)
