import React from "react";
import API from "../../utils/API";
import {Segment, Button, Container, Form, Image, Input, Grid, Divider, Rail} from "semantic-ui-react";
import Warnings from "../Warnings/Warnings";
import DividerC from "./../../components/Divider/Divider";

const DEFAULT_STATE = {
    lastname: "",
    firstname: "",
    email: "",
    username: "",
    password: "",
    cpassword: "",
    warnings: [],
    w_lastname: '',
    w_firstname: '',
    w_email: '',
    w_username: '',
    w_password: '',
    w_cpassword: '',
};

const DEFAULT_ERRORS = {
    w_lastname: '',
    w_firstname: '',
    w_email: '',
    w_username: '',
    w_password: '',
    w_cpassword: '',
};

// Validate mail
function validateEmail(email) {
    let re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(email);
}
// Validate password
function validatePassword(password) {
    let re = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
    return re.test(password);
}
// Validate username
function validateUsername(username){
    let re = /^(?=.{4,15}$)(?![_.])(?!.*[_.]{2})[a-zA-Z0-9._]+(?<![_.])$/;
    return re.test(username)
}
// Validate First Name and Last Name
function validateFirstName(name){
    let re = /^(?=.{1,20}$)[a-z]+(?:['_.\s][a-z]+)*$/i;
    return re.test(name);
}

class Signup extends React.Component {
    constructor (props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this.warnings = {...DEFAULT_ERRORS};
    }

    send = async() => {
        const {lastname, firstname, email, username, password, cpassword} = this.state;
        let errors = false;
        this.warnings = {...DEFAULT_ERRORS};
        if (!validateEmail(email))
            this.warnings.w_email = "Please enter a valid email.";
        if (!validatePassword(password))
            this.warnings.w_password = "Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters.";
        if (cpassword.localeCompare(password) !== 0)
            this.warnings.w_cpassword = "Password does not match.";
        if (!validateUsername(username))
            this.warnings.w_username = "Please use a valid username.";
        if (!validateFirstName(firstname))
            this.warnings.w_firstname = "Please use a valid first name.";
        if (!validateFirstName(lastname))
            this.warnings.w_lastname = "Please use a valid last name.";
        // Check if warning is set
        if (Object.keys(this.warnings).map((data) => {
            if (data.length > 0)
                errors = true;
        }))
        // Stop function if errors is true
        if (errors === false){
            this.setState({loading: true});
            try {
                const { data } = await API.signup(lastname, firstname, email, username, password, cpassword)
                if (data.status === 200) this.props.history.push('/');
                else throw new Error (data.warnings);
            } catch (warnings) {
                this.setState({warnings: warnings});
            }
            this.setState({loading: false});
        }
        else
            this.setState({...this.warnings});
    }

    handleChange = (event) => {
        const key = "w_" + event.target.id;
        if (this.warnings[key].length > 0)
            this.warnings[key] = '';
        this.setState({[event.target.id]: event.target.value});
    };


    render() {
        const {w_firstname, w_lastname, w_email, w_password, w_username, w_cpassword} = this.warnings;
        return (
            <Container className="signupModal">
                <div className="shapeSignup"></div>
                <Divider hidden />
                <Image className="img-fluid"
                       src="/img/MatchApp-Logo.png"
                       alt="Responsive image"
                       size="medium"
                       centered/>
                <div className="signupWarnings">
                    <Warnings data={this.state.warnings} />
                </div>
                <Divider hidden />
                <Container textAlign='center'>
                    <h1 className="signuph1">Create an account</h1>
                </Container>
                <DividerC />
                <Form className="signupForm">
                    <Form.Group widths='equal'>
                            <Form.Input
                                   onKeyDown={this.handleKeyDown}
                                   error={w_firstname.length > 0 ? w_firstname : null}
                                   label="First Name"
                                   id="firstname"
                                   icon='user'
                                   iconPosition='left'
                                   autoFocus
                                   onChange={this.handleChange}
                                   size="huge"
                                   placeholder="First Name"
                                   autoComplete="off"
                                   maxLength="13"
                                   required />
                            <Form.Input
                                   error={w_lastname.length > 0 ? w_lastname : null}
                                   label="Last Name"
                                   id="lastname"
                                   icon='user'
                                   iconPosition='left'
                                   autoFocus
                                   onChange={this.handleChange}
                                   size="huge"
                                   placeholder="Last Name"
                                   autoComplete="off"
                                   maxLength="13"
                                   required />
                    </Form.Group>
                    <Form.Group widths='equal' onSubmit={this.send}>
                        <Form.Input
                                   error={w_email.length > 0 ? w_email : null}
                                   label="Email"
                                   id="email"
                                   icon='mail'
                                   iconPosition='left'
                                   autoFocus
                                   type="email"
                                   onChange={this.handleChange}
                                   size="huge"
                                   placeholder="Email"
                                   autoComplete="off"
                                   maxLength="30"
                                   required />
                        <Form.Input
                               error={w_username.length > 0 ? w_username : null}
                               label="Choose a username"
                               id="username"
                               icon='user circle'
                               iconPosition='left'
                               autoFocus
                               onChange={this.handleChange}
                               size="huge"
                               placeholder="Choose a username"
                               autoComplete="off"
                               required
                               maxLength="23"
                        />
                    </Form.Group>
                    <Form.Group widths='equal'>
                        <Form.Input
                               error={w_password.length > 0 ? w_password : null}
                               label="Password"
                               icon='lock'
                               iconPosition='left'
                               id="password"
                               onChange={this.handleChange}
                               type="password"
                               size="huge"
                               placeholder="Password"
                               required/>
                        <Form.Input
                               error={w_cpassword.length > 0 ? w_cpassword : null}
                               label="Confirm Password"
                               icon='lock'
                               iconPosition='left'
                               id="cpassword"
                               onChange={this.handleChange}
                               type="password"
                               size="huge"
                               placeholder="Confirm Password"
                               required
                        />
                    </Form.Group>
                    <Grid.Row centered>
                        <Button className="signupButton"
                                loading={this.state.loading}
                                size='huge'
                                type="submit"
                                onClick={this.send}>
                            Register
                        </Button>
                    </Grid.Row>
                </Form>
                <DividerC />
                <Grid textAlign="center">
                    <Grid.Row centered>
                        <Button className="signupButton"
                                loading={this.state.loading}
                                size='huge'
                                type="submit"
                                onClick={this.send}>
                            Register
                        </Button>
                    </Grid.Row>
                    <Grid.Row centered>
                        <div className="signupNoAccount">
                            <p>Have an account ? <a href="/login">Log in</a></p>
                        </div>
                    </Grid.Row>
                </Grid>
                <Divider hidden />
            </Container>
        );
    }
}

export default Signup;
