import React from 'react';
import { Modal, Button } from 'semantic-ui-react';
import API from './../../utils/API';
import { store } from 'react-notifications-component';

class ModalUserActions extends React.Component{

    constructor(props){
        super(props);
        this._mounted = false;
    }

    componentDidMount() {
        this._mounted = true;
    }
    close = () => {this._mounted && this.setState({open: false})};
    doReport = async() => {
        await API.reportuser(this.props.userID)
            .then(response => {
                if (response.status === 200){
                    store.addNotification({
                        title: 'User has been reported',
                        type: "warning",
                        insert: "top",
                        container: "top-right",
                        animationIn: ["animated", "fadeIn"],
                        animationOut: ["animated", "fadeOut"],
                        dismiss: {
                            duration: 10000,
                            onScreen: true
                        }
                    });
                }
            })
            .catch (err => { console.log(err)});
    };
    render(){
        return (
            <div>
                <Modal size='tiny' open={this.props.openReport} onClose={this.props.closeReport}>
                    <Modal.Header>Report user</Modal.Header>
                    <Modal.Content>
                        <p>Are you sure you want to report this user ?</p>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button
                            negative
                            onClick={this.props.closeReport}>
                            No
                        </Button>
                        <Button
                            positive
                            icon='checkmark'
                            labelPosition='right'
                            content='Yes'
                            onClick={this.props.doReport}
                        />
                    </Modal.Actions>
                </Modal>
            </div>
        )
    }
}

export default ModalUserActions;
