import React from 'react';
import {Modal, Input } from 'semantic-ui-react'

class ModalChat extends React.Component {
    constructor(props){
        super(props);
        this._mounted = false;
        this.state = {
            showModal: false,
            user: []
        }
    }

    componentDidMount() {
        this._mounted = true;
    }

    // Outside Ref
    handleOpen = (user) => {this._mounted && this.setState({showModal: true, user: user})};
    handleClose = () => {this._mounted && this.setState({showModal: false, user: []})};

    render(){
        const { showModal, user } = this.state;
        return (
            user ?
                <Modal
                    size={'tiny'}
                    open={showModal}
                    onClose={this.handleClose}
                    closeIcon>
                    <Modal.Header>Chat with {user.firstname}</Modal.Header>
                    <Modal.Content scrolling className={'ModalChatContent'}>
                        <div className={'inputChat'}>
                            <Input action={{ icon: 'search' }} placeholder='Type a message...' />
                        </div>
                    </Modal.Content>
                </Modal> : null
        )
    }
}

export default ModalChat;
