import React from 'react';
import UserMiniCard from "../../components/UserMiniCard/UserMiniCard";
import API from './../../utils/API';
import {Dimmer, Loader, Grid, Rail,Segment} from "semantic-ui-react";

const DEFAULT_STATE = {
    users: [],
    warnings: [],
    loading: false,
    distanceRange: 100,
};

class Wall extends React.Component {

    state = {...DEFAULT_STATE};

    async componentDidMount() {
        // this.setState({loading: true});
        // await API.getUsers()
        //     .then(response => {
        //         if (response.status === 200) {
        //             this.setState({users: response.data.users});
        //         }
        //     })
        //     .catch(error => {
        //         console.log(error.response);
        //         this.setState({warnings : error.response.data.warnings})
        //     })
        // this.setState({loading: false})
    }

    handleRange = (event) => {
        this.setState({[event.target.id]: event.target.value})
    };
    render() {
        const { users, loading, distanceRange } = this.state;
        // if (users && users.length)
            return (
                <div className="WallContainer">
                    <Dimmer active={loading}>
                        <Loader size='massive'/>
                    </Dimmer>
                    <Grid celled='internally' stackable>
                        <Grid.Column textAlign='left' width={4}>
                            <Grid.Row>
                            <h1 className="DistanceRangeTitle">Distance</h1>
                            <span className="RangeValue">{distanceRange} <strong>km</strong></span>
                            </Grid.Row>
                            <Grid.Row>
                                <div className="ui slider range">
                                    <input
                                        type="range"
                                        min="0"
                                        max="500"
                                        id="distanceRange"
                                        onChange={this.handleRange} />
                                </div>
                            </Grid.Row>
                        </Grid.Column>
                        <Grid.Column width={10}>

                        </Grid.Column>
                    </Grid>
                    {/*<UserMiniCard*/}
                    {/*    users={users}*/}
                    {/*/>*/}
                </div>
            );
        return null;
    }
}


export default Wall;
