import React from 'react';
import { List, Image } from 'semantic-ui-react';
import API from '../../../utils/API';

const DEFAULT_STATE = {
    notifications: [],
};

export default class NotificationsHistory extends React.Component{

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this._mounted = false;
    }

    componentDidMount = async() => {
        this._mounted = true;
        await API.getNotifications()
            .then(response => {
                if (response.status === 200)
                    this._mounted && this.setState({notifications: response.data.notifications}) && console.log(response.data.notifications);
            })
            .catch(e => console.log(e));
    };

    componentWillUnmount() {
        this._mounted = false;
    }

    renderSwitchType(type) {
        switch (type) {
            case 1:
                return 'visited your profile  ';
            case 2:
                return 'send you a message  ';
            case 3:
                return 'liked you back  ';
            case 4:
                return 'remove his like  ';
        }
    }
    notifmap = (notifications) => {
        notifications.map((obj, i) => {
            return (
                <List relaxed>
                    <List.Item key={i}>
                        <Image avatar src={obj.img_link}/>
                        <List.Content>
                            <List.Header as='a'>{obj.firstname}</List.Header>
                            <List.Description>
                                {this.renderSwitchType(obj.type)}
                                on {obj.date}.
                            </List.Description>
                        </List.Content>
                    </List.Item>
                </List>
            )
        });
    };

    render(){
        return (
            <div>
                {this.notifmap(this.state.notifications)}
            </div>
        )
    }
}
