import React from 'react';
import {Segment,Sidebar, Header, Icon, Menu, Grid, Image, Button, Divider, SidebarPushable} from 'semantic-ui-react'
import { Redirect } from 'react-router-dom'
import { withRouter } from 'react-router';
import API from '../../utils/API'
import Aux from "../../hoc/Aux";
class NavbarMenu extends React.Component {

    state = {
        activeItem: null,
        redirect: null,
        connected: false,
    }

    constructor(props){
        super(props);
    }

    componentDidMount() {
        this.setState({activeItem: this.props.location.pathname})
        API.withAuth()
            .then(res => {
                if (res.status === 200) {
                    this.setState({connected: true});
                }
                else
                    throw new Error(res.error);
            })
            .catch(() => {this.setState({connected: false});});
    }

    handleItemClick = (e, { name }) => {
        this.setRedirect(name)
        this.setState({ activeItem: name })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to={this.state.redirect} />
        }
    }
    setRedirect = (name) => {
        this.setState({redirect: name})
    }
    disconnect = () => {
        fetch('http://localhost:3000/logout', {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => {
                if (res.status === 200) {
                    this.setState({connected: false})
                    this.props.history.push('/login');
                } else {
                    const error = new Error(res.error);
                    throw error;
                }
            })
            .catch(err => {
                console.error(err);
                this.setState({warnings: ["Wrong username or password"]});
            });
    };
    isConnected = () => {
        const { activeItem } = this.state
        if (this.state.connected === false)
            return (
                <Aux>
                <Menu.Item
                    name='/signup'
                    active={activeItem === '/signup'}
                    onClick={this.handleItemClick} >
                    <Icon name='signup' />
                    Sign up
                </Menu.Item>
                <Menu.Item
                    name='/login'
                    active={activeItem === '/login'}
                    onClick={this.handleItemClick} >
                    <Icon name='user' />
                    Log in
                </Menu.Item>
                </Aux>
            )
        else {
            return (
                <div>
                    <Menu.Item
                        name='/logout'
                        active={activeItem === '/logout'}
                        onClick={this.disconnect} >
                        <Icon name='logout' />
                        Log out
                    </Menu.Item>
                </div>
            )
        }
    }
    render(){
        const { activeItem } = this.state
        return(
            <div>
            <Sidebar.Pushable as={Segment}>
                <Sidebar
                    as={Menu}
                    animation='overlay'
                    icon='labeled'
                    inverted
                    vertical
                    visible
                    width='thin'
                >
                    <Menu.Item as='a'>
                        <Icon name='home' />
                        Home
                    </Menu.Item>
                    <Menu.Item as='a'>
                        <Icon name='gamepad' />
                        Games
                    </Menu.Item>
                    <Menu.Item as='a'>
                        <Icon name='camera' />
                        Channels
                    </Menu.Item>
                </Sidebar>

                <Sidebar.Pusher>
                    <Segment basic>
                        <Header as='h3'>Application Content</Header>
                        <Image src='/images/wireframe/paragraph.png' />
                    </Segment>
                </Sidebar.Pusher>
            </Sidebar.Pushable>
                <Menu inverted icon='labeled' stackable size={"medium"} className="Menu">
                    {this.renderRedirect()}

                    <Menu.Item className="Logo">
                        <Image
                            className="NavbarLogo"
                            src='./../../img/MatchApp-Logo.png'
                            size={"small"}/>
                    </Menu.Item>
                    <Menu.Menu icon='labeled'>
                        <Menu.Item
                            name='/wall'
                            active={activeItem === '/wall'}
                            onClick={this.handleItemClick} >
                            <Icon name='gratipay' />
                            Wall
                        </Menu.Item>
                        <Menu.Item
                            name='/'
                            active={activeItem === '/'}
                            onClick={this.handleItemClick} >
                            <Icon name='user' />
                            My profil
                        </Menu.Item>
                        <Menu.Item
                            name='/messages'
                            active={activeItem === '/messages'}
                            onClick={this.handleItemClick}>
                            <Icon name='conversation' />
                            Messages
                        </Menu.Item>
                    </Menu.Menu>
                    <Menu.Item
                        name='/notifications'
                        active={activeItem === '/notifications'}
                        onClick={this.handleItemClick}>
                        <Icon name='bell' />
                        Notifications
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        {this.isConnected()}
                    </Menu.Menu>
                </Menu>
            </div>
        )
    }
}
export default withRouter(NavbarMenu)
