import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";
import Aux from './hoc/Aux';
import Signup from "./components/Signup/Signup.js";
import Wall from './containers/Wall/Wall';
import Login from "./components/Login/Login";
import EditProfile from "./components/EditProfil/EditProfil";
import withAuth from "./utils/withAuth";
import './App.scss';
import BootstrapMenu from './components/Menu/BootstrapMenu';
import Footer from './containers/Footer/Footer';

class App extends Component {

    state = {
        isConnected: false,
    };

    isConnected = (bool) => {
        console.log(bool);
        this.setState(bool)
    };

    render() {
        return (
            <Aux>
                <BootstrapMenu isConnected={this.state.isConnected} />
                <div className="content">
                    <Switch>
                        <Route exact path="/wall" component={withAuth(Wall)} />
                        <Route exact path="/profile" component={withAuth(EditProfile)} />
                        <Route exact path="/signup" component={Signup} />
                        <Route exact path="/login" isConnected={ (bool) => this.isConnected(bool)} component={Login} />
                    </Switch>
                </div>
                <Footer />
            </Aux>
        );
    }
}
export default App;
