import {Divider, Grid, Image, Label, Modal, Segment, Icon} from "semantic-ui-react";
import Button from "semantic-ui-react/dist/commonjs/elements/Button";
import React from "react";
import classnames from 'classnames';
import ModalUserActions from "./ModalUserActions";
import API from "../../utils/API";

const DEFAULT_STATE = {
    openHide: false,
    openReport: false,
    openFake: false,
    liked: false,
    user: {
        lastname: '',
        firstname: '',
        interests: [],
        gender: [],
        interested: [],
        imgs: [],
        country: '',
        bio: '',
        age: '',
        likes: ''
    },
};

class ModalUser extends React.Component {

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this._mounted = false;
    }

    componentDidMount = async() => {
        this._mounted = true;
        await API.getUserInterests()
            .then(response => {
                if (response.status === 200)
                    this._mounted && this.setState({interests: response.data.interests})
            });
    };
    componentWillUnmount() {
        this._mounted && this.setState({...DEFAULT_STATE});
        this._mounted = false;
    }
    // Load user
    componentDidUpdate= async(prevProps) => {
        if(this.props.showModal !== prevProps.showModal && this.props.userid !== prevProps.userid) {
            API.checkUserLike(userIdFocus)
                .then(response => {
                    if (response.status === 200 && typeof response.data.liked != 'undefined')
                        this._mounted && this.setState({liked: response.data.liked, showModal: true, userIdFocus: userIdFocus});
                });
            API.getUserIdProfile(this.props.userid)
                .then(async (res) => {
                    if (res.status === 200) {
                        this._mounted && this.setState({user: res.data.user, showModal: true});
                    }
                })
        }
    };
    // Modal
    handleClose = () => {
        this.props.handleClose();
        this._mounted && this.setState({ user: null, liked: false})
    };
    // Modal user actions
    closeReport = () => { this._mounted && this.setState({openReport: false})};
    openReport = () => {this._mounted && this.setState({openReport: true})};
    closeFake = () => { this._mounted && this.setState({openFake: false})};
    openFake = () => {this._mounted && this.setState({openFake: true})};
    closeHide = () => { this._mounted && this.setState({openHide: false})};
    openHide = () => {this._mounted && this.setState({openHide: true})};
    render() {
        const { userid, showModal, handleClose } = this.props;
        const {user, liked, openReport, openFake, openHide } = this.state;
        return (
            <div>
                <ModalUserActions
                    openReport={openReport}
                    closeReport={this.closeReport}
                    openFake={openFake}
                    closeFake={this.closeFake}
                    openHide={openHide}
                    closeHide={this.closeHide}
                    userID={userid}
                />
                <Modal
                    dimmer={"blurring"}
                    size={'tiny'}
                    open={showModal}
                    onClose={handleClose}
                    className="ModalCard">
                    <Modal.Header className="CardHeader"><h1
                        className="CardHeaderTile"> {user.firstname} {user.lastname}, <strong>{user.age}</strong></h1>
                    </Modal.Header>
                    <Modal.Content className="ModalProfilView">
                        <Modal.Header></Modal.Header>
                        <Divider hidden/>
                        <Grid centered columns={2}>
                            <Grid.Column className="RowImages">
                                <Image rounded size='medium' src={user.imgs[0]}/>
                            </Grid.Column>
                            <Grid.Row className="RowImages" centered columns={4}>
                                {user.imgs.map((img, i) => {
                                    if (i > 0)
                                        return (
                                            <Grid.Column key={i}>
                                                <Image rounded size='small' src={user.imgs[i]}/>
                                            </Grid.Column>
                                        );
                                    return null;
                                })}
                            </Grid.Row>
                        </Grid>
                        <Modal.Description>
                            <Segment className="BioSegment" textAlign="center" inverted>
                                <p>{user.bio}</p>
                            </Segment>
                            <Segment basic textAlign={'center'}>
                                <Button size='small' id='like' onClick={() => this.props.userLike(user.user_id)}>
                                    <Icon name='heart'/>
                                    {liked ? 'Unlike' : 'Like'}
                                </Button>
                                <Button size='small' id='hide' onClick={this.openHide}>
                                    <Icon name='user x'/>
                                    Hide
                                </Button>
                                <Button size='small' id='fake' onClick={this.openFake}>
                                    <Icon name='user secret'/>
                                    Fake
                                </Button>
                                <Button size='small' id='blockuser' onClick={this.openReport}>
                                    <Icon name='warning'/>
                                    Signal
                                </Button>
                            </Segment>
                            <Divider hidden/>
                            <Label.Group size='small'>
                                <p/>
                                {user.interests.map((data, i) => {
                                    return (
                                        <Label
                                            className={user.interests.find(interest => interest === data) ? classnames("interestLabel","sameInterest") : "interestLabel"}
                                            id={i}
                                            key={i}
                                            data={data}>
                                            <strong>#</strong>{data}</Label>
                                    )
                                })}
                            </Label.Group>
                        </Modal.Description>
                    </Modal.Content>
                </Modal>
            </div>
        )
    }
}

export default ModalUser;
