import _ from 'lodash'
import faker from 'faker'
import React, { Component } from 'react'
import { Dropdown } from 'semantic-ui-react'
import API from "../../../utils/API";

const getOptions = () =>
    _.times(3, () => {
        const name = faker.name.findName()
        return { key: name, text: name, value: _.snakeCase(name) }
    });

class SearchTags extends Component {
    state = {
        isFetching: false,
        multiple: true,
        search: true,
        searchQuery: null,
        value: [],
        DATA: [],
    };

    componentDidMount = async() => {
        this.setState({loading: true, interests: []});
        await API.getInterests()
            .then(response => {this.setState({DATA: response.data.results});
            });
    };

    handleChange = (e, { value }) => this.setState({ value })
    handleSearchChange = (e, { searchQuery }) => this.setState({ searchQuery })

    render() {
        const { multiple, DATA, isFetching, search, value } = this.state;

        return (
            <div>
                <Dropdown
                    fluid
                    selection
                    multiple={multiple}
                    search={search}
                    options={DATA}
                    value={value}
                    placeholder='Add Users'
                    onChange={this.handleChange}
                    onSearchChange={this.handleSearchChange}
                    disabled={isFetching}
                    loading={isFetching}
                />
            </div>
        )
    }
}

export default SearchTags;
