import React from 'react';
import { Button, Header, Icon, Modal, Image } from 'semantic-ui-react'
import _ from 'lodash'

class ModalChat extends React.Component {
    constructor(props){
        super(props);
        this._mounted = false;
        this.state = {
            showModal: false,
            user: []
        }
    }

    componentDidMount() {
        this._mounted = true;
    }

    // Outside Ref
    handleOpen = (user) => {this._mounted && this.setState({showModal: true, user: user})};
    handleClose = () => {this._mounted && this.setState({showModal: false, user: []})};

    render(){
        const { showModal, user } = this.state;
        return (
            user ?
                <Modal
                    size={'tiny'}
                    open={showModal}
                    onClose={this.handleClose}
                    closeIcon>
                    <Modal.Header>Chat with {user.firstname} {user.lastname} </Modal.Header>
                    <Modal.Content image scrolling>
                        <Image size='medium' src={user.img_link} wrapped />

                        <Modal.Description>
                            <Header>Modal Header</Header>
                            <p>
                                This is an example of expanded content that will cause the modal's
                                dimmer to scroll
                            </p>
                        </Modal.Description>
                    </Modal.Content>
                </Modal> : null
        )
    }
}

export default ModalChat;
