import React from 'react';
import openSocket from 'socket.io-client';
import ModalUserLike from "./ModalUserLike/ModalUserLike";
const  socket = openSocket('http://localhost:3002');

class Notifications extends React.Component {

    state = {
        showModal: false,
        userID: null,
    };
    constructor() {
        super();
    }

    componentDidMount() {
        socket.on("sendlike", (userID) => {
            this.setState({userID: userID, showModal: true})
        })
    }

    handleClose = () => this.setState({ userID: null, showModal: false})

    render() {
        const { showModal, userID } = this.state;
        return (
            <div className="Notifications">
                <ModalUserLike showModal={showModal} userID={userID} handleClose={this.handleClose}/>
            </div>
        );
    }
}

export default Notifications;
