import React from "react";
import Warnings from "../Warnings/Warnings";
import Divider from "../../components/Divider/Divider";
import VALIDATE from "../../utils/validation";
import API from "../../utils/API";
import { Input, Container, Image, Form, Button } from 'semantic-ui-react';

const DEFAULT_STATE = {
    email: "",
    password: "",
    warnings: [],
    loading: false,
    w_email: '',
};

const DEFAULT_ERRORS = {
    w_email: ''
};

class Login extends React.Component {

    constructor (props) {
        super(props);
        this.state = {...DEFAULT_STATE};
        this.warnings = {...DEFAULT_ERRORS}
    };

    send = () =>  {
        // Clear warnings
        this.warnings = {...DEFAULT_ERRORS}
        const { email, password } = this.state;
        // Check Validity
        this.setState({loading: true}, async() => {
            if (!VALIDATE.validateEmail(email))
                this.warnings.w_email = "Please enter a valid email";
            if (!VALIDATE.validatePassword(password))
                this.warnings.w_password = "Please enter a valid password";
            if (!VALIDATE.checkWarnings(this.warnings)) {
                this.setState({...this.warnings});
            }
            else {
                await API.login(email, password)
                    .then(response => {
                        if (typeof response !== 'undefined'
                            && typeof response.data !== 'undefined' && typeof response.data.status !== 'undefined')
                            this.props.history.push('/');
                    })
                    .catch(error => {
                        if (typeof error.response !== 'undefined'
                            && typeof error.response.data !== 'undefined' && typeof error.response.data.w_email !== 'undefined')
                            this.setState({...error.response.data.w_email});
                    })
            }
        });
        // Unset Loader
        this.setState({loading: false});
    };

    handleChange = (event) => {
        const key = "w_" + event.target.id;
        if (this.state[key].length > 0)
            this.setState({[key]: ''});
        this.setState({[event.target.id]: event.target.value});
    }


    render() {
        const {loading, w_email} = this.state;
        return (
            <Container className="loginModal">
                <Divider />
                     <Image className="img-fluid"
                            src="/img/MatchApp-Logo.png"
                            alt="Responsive image"
                            size="medium"
                            centered />
                    <div className="loginWarnings">
                            <Warnings data={this.state.warnings}/>
                    </div>
                    <div className="shape"></div>
                    <Divider />
                    <h1 className="loginh1">User Login</h1>
                        <Form className="loginForm">
                            <Form.Field>
                                <Input
                                       error={w_email.length > 0 ? w_email : null}
                                       id="email"
                                       icon='users'
                                       iconPosition='left'
                                       autoFocus
                                       type="email"
                                       onChange={this.handleChange}
                                       size="huge"
                                       placeholder="Email" />
                            </Form.Field>
                            <Form.Field className="inputPassword">
                                <Input
                                    icon='lock'
                                    iconPosition='left'
                                    id="password"
                                    onChange={this.handleChange}
                                    type="password"
                                    size="huge"
                                    placeholder="Password" />
                            </Form.Field>
                            <Divider />
                            <Button className="loginButton"
                                    loading={loading} size='huge'
                                    type="submit"
                                    onClick={this.send}>
                                Log in
                            </Button>
                        </Form>
                    <Divider />
                    <div className="loginNoAccount">
                        <p className="loginForgot">
                            <a href="/signup"><strong>Forgot password ?</strong></a>
                        </p>
                        <p>No account ? <a href="/signup">Register</a></p>
                    </div>
                <Divider />
            </Container>
        );
    }
}

export default Login;
