import React, { Component } from 'react'
import { Dropdown } from 'semantic-ui-react'
import API from "../../../utils/API";
const DEFAULT_STATE = {
        isFetching: false,
        multiple: true,
        search: true,
        value: [],
        DATA: [],
}
class SearchTags extends Component {
    state = {
        isFetching: false,
        multiple: true,
        search: true,
        value: [],
        DATA: [],
    };

    componentDidMount = async() => {
        this._mounted = true;
        this._mounted && this.setState({loading: true, interests: []});
        await API.getInterests()
            .then(response => {
                this.setState({DATA: response.data.results.map((obj, i) => {
                        return { key: i, text: obj.title, value: obj.title }
                    })});
            });
    };
    componentWillUnmount() {
        this._mounted && this.setState({...DEFAULT_STATE});
        this._mounted = false;
    }

    handleChange = (e, { value }) => this.setState({ value });

    render() {
        const { multiple, DATA, search, value } = this.state;

        return (
            <div className={'SearchTags'}>
                <Dropdown
                    id={'dropdownSearchTags'}
                    fluid
                    selection
                    multiple={multiple}
                    search={search}
                    options={DATA}
                    value={value}
                    placeholder='Add tags'
                    onChange={this.handleChange}
                />
            </div>
        )
    }
}

export default SearchTags;
