import React from "react";
import API from "../../utils/API";
import {Segment, Button, Container, Form, Image, Input, Grid, Divider, Rail} from "semantic-ui-react";
import Warnings from "../Warnings/Warnings";
import DividerC from "./../../components/Divider/Divider";

const DEFAULT_STATE = {
    lastName: "",
    firstName: "",
    email: "",
    username: "",
    password: "",
    cpassword: "",
    warnings: [],
};

const DEFAULT_ERRORS = {
    w_lastname: '',
    w_firstname: '',
    w_email: '',
    w_username: '',
    w_password: '',
    w_cpassword: '',
};

class Signup extends React.Component {
    constructor (props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this.warnings = {...DEFAULT_ERRORS};
    }

    send = () => {
        const {lastName, firstName, email, username, password, cpassword} = this.state;
        let errors = false;
        if (!email || email.length === 0)
            this.warnings.w_email = "Please fill email field";
        if (!password || password.length === 0)
            this.warnings.w_password = "Please fill password field";
        if (cpassword.localeCompare(password) !== 0)
            this.warnings.w_cpassword = "Passwords does not match";
        if (!username || username.length === 0)
            this.warnings.w_username = "Please fill username field";
        if (!firstName || firstName.length === 0)
            this.warnings.w_firstname = "Please fill First Name field";
        if (!lastName || lastName.length === 0)
            this.warnings.w_lastname = "Please fill Last Name field";
        // Check if warning is set
        if (Object.keys(this.warnings).map((data) => {
            if (data.length > 0)
                errors = true;
        }))
        // Stop function if errors is true
        if (errors === true)
            return;
        else {
            this.setState({loading: true});
            try {
                API.signup(lastName, firstName, email, username, password, cpassword)
                    .then(res => {
                        if (res.status === 200)
                            this.props.history.push('/');
                        else
                            throw new Error (res.data.warnings);
                    })
                    .catch(err => {
                        this.setState({warnings: ["Error"]});
                    });
                this.setState({send: true});
            } catch (error) {
                console.error(error);
            }
            this.setState({loading: false});
        };
    };

    handleChange = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        });
    };


    render() {
        return (
            <Container className="signupModal">
                <div className="shapeSignup"></div>
                <Divider hidden />
                <Image className="img-fluid"
                       src="/img/MatchApp-Logo.png"
                       alt="Responsive image"
                       size="medium"
                       centered/>
                <div className="signupWarnings">
                    <Warnings data={this.state.warnings} />
                </div>
                <Divider hidden />
                <Container textAlign='center'>
                    <h1 className="signuph1">Create an account</h1>
                </Container>
                <DividerC />
                <Form className="signupForm">
                    <Form.Group widths='equal'>
                            <Form.Input
                                   label="First Name"
                                   id="firstName"
                                   icon='user'
                                   iconPosition='left'
                                   autoFocus
                                   onChange={this.handleChange}
                                   size="huge"
                                   placeholder="First Name"
                                   autoComplete="off"
                                   maxLength="13"
                                   required
                            />
                            <Form.Input
                                   label="Last Name"
                                   id="lastName"
                                   icon='user'
                                   iconPosition='left'
                                   autoFocus
                                   onChange={this.handleChange}
                                   size="huge"
                                   placeholder="Last Name"
                                   autoComplete="off"
                                   maxLength="13"
                                   required
                            />
                    </Form.Group>
                    <Form.Group widths='equal'>
                        <Form.Input
                                   label="Email"
                                   id="email"
                                   icon='mail'
                                   iconPosition='left'
                                   autoFocus
                                   type="email"
                                   onChange={this.handleChange}
                                   size="huge"
                                   placeholder="Email"
                                   autoComplete="off"
                                   maxLength="30"
                                   required
                         />
                        <Form.Input
                               label="Choose a username"
                               id="username"
                               icon='user circle'
                               iconPosition='left'
                               autoFocus
                               onChange={this.handleChange}
                               size="huge"
                               placeholder="Choose a username"
                               autoComplete="off"
                               required
                               maxLength="23"
                        />
                    </Form.Group>
                    <Form.Group widths='equal'>
                        <Form.Input
                               label="Password"
                               icon='lock'
                               iconPosition='left'
                               id="password"
                               onChange={this.handleChange}
                               type="password"
                               size="huge"
                               placeholder="Password"
                               required/>
                        <Form.Input
                               label="Confirm Password"
                               icon='lock'
                               iconPosition='left'
                               id="cpassword"
                               onChange={this.handleChange}
                               type="password"
                               size="huge"
                               placeholder="Confirm Password"
                               required
                        />
                    </Form.Group>
                    </Form>
                <DividerC />
                <Grid textAlign="center">
                    <Grid.Row centered>
                        <Button className="signupButton"
                                loading={this.state.loading}
                                size='huge'
                                onClick={this.send}>
                            Register
                        </Button>
                    </Grid.Row>
                    <Grid.Row centered>
                        <div className="signupNoAccount">
                            <p>Have an account ? <a href="/login">Log in</a></p>
                        </div>
                    </Grid.Row>
                </Grid>
                <Divider hidden />
            </Container>
        );
    }
}

export default Signup;
