import React from 'react';
import { ButtonToolbar, Navbar, Button, Nav, ButtonGroup } from 'react-bootstrap';
import '../../Assets/global-styles/bootstrap-iso.css';
import { Icon } from "semantic-ui-react";
import API from "../../utils/API";
import {Redirect} from "react-router";
import { withRouter } from 'react-router';
import Aux from "../../hoc/Aux";

class NavbarBootstrap extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            activeItem: null,
            redirect: null,
            connected: false,
        };
    }

    componentDidMount() {
        this.setState({activeItem: this.props.location.pathname});
        API.withAuth()
            .then(res => {
                if (res.status === 200) {
                    this.setState({connected: true});
                }
                else
                    throw new Error(res.error);
            })
            .catch(() => {this.setState({connected: false})});
    }

    handleItemClick = (e, { name }) => {
        console.log(name);
        this.setRedirect(name);
        this.setState({ activeItem: name })
    };

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to={this.state.redirect} />
        }
    };
    setRedirect = (name) => {this.setState({redirect: name})};

    disconnect = () => {
        fetch('http://localhost:3000/logout', {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => {
                if (res.status === 200) {
                    this.setState({connected: false});
                    this.props.history.push('/login');
                } else {
                    const error = new Error(res.error);
                    throw error;
                }
            })
            .catch(err => {
                this.setState({warnings: ["Wrong username or password"]});
            });
    };

    isConnected = () => {
        const { activeItem } = this.state;
        if (this.state.connected === false)
            return (
                <Aux>
                    <ButtonToolbar className='mr-3'>
                        <ButtonGroup className='mt-3 mb-3'>
                            <Button
                                className={activeItem === '/login' ? "activeItemButton" : null}
                                id='/login'
                                size="md"
                                variant="outline-primary"
                                onClick={this.handleItemClick}>Log in</Button>
                            <Button
                                className={activeItem === '/signup' ? "activeItemButton" : null}
                                id='/signup'
                                variant="outline-primary"
                                onClick={this.handleItemClick}>Sign up</Button>
                        </ButtonGroup>
                    </ButtonToolbar>
                </Aux>
            );
        else {
            return (
                <div>
                    <ButtonToolbar className='mr-3'>
                        <ButtonGroup className='mt-3 mb-3'>
                            <Button
                                size="md"
                                variant="outline-primary"
                                onClick={this.disconnect}>Log out</Button>
                        </ButtonGroup>
                    </ButtonToolbar>
                </div>
            )
        }
    };
    render() {
        const { activeItem } = this.state;
        return (
            <div className='bootstrap-iso'>
                {this.renderRedirect()}
                <div className="Menu">
                    <Navbar sticky="top" collapseOnSelect expand="sm">
                        <Navbar.Brand
                            className="ml-3"
                            id='/wall'
                            onClick={this.handleItemClick}>
                            <img
                                src='./../../img/MatchApp-Logo.png'
                                height="50"
                                alt="MatchApp Logo"/>
                        </Navbar.Brand>
                        <Navbar.Toggle/>
                        <Navbar.Collapse>
                            <Nav className='mr-auto'>
                                <Nav.Link
                                    className={activeItem === '/wall' ? "activeItem" : null}
                                    id='/wall'
                                    onClick={this.handleItemClick}>
                                    <Icon size='large' name='gratipay' />
                                    <span className="itemMenu">Wall</span>
                                </Nav.Link>
                                <Nav.Link
                                    className={activeItem === '/profile' ? "activeItem" : null}
                                    id='/profile'
                                    onClick={this.handleItemClick}>
                                    <Icon size='large' name='user' />
                                    <span className="itemMenu">Profile</span>
                                </Nav.Link>
                                <Nav.Link
                                    className={activeItem === '/messages' ? "activeItem" : null}
                                    id='/messages'
                                    onClick={this.handleItemClick}>
                                    <Icon size='large' name='conversation' />
                                    <span className="itemMenu">Messages</span>
                                </Nav.Link>
                                <Nav.Link
                                    className={activeItem === '/notifications' ? "activeItem" : null}
                                    id='/notifications'
                                    onClick={this.handleItemClick}>
                                    <Icon size='large' name='bell' />
                                    <span className="itemMenu">Notifications</span>
                                </Nav.Link>
                            </Nav>
                            <Nav>
                                {this.isConnected()}
                            </Nav>
                        </Navbar.Collapse>
                    </Navbar>
                </div>
            </div>
        )
    }
}

export default withRouter(NavbarBootstrap);
