import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';
import {Grid, Icon} from "semantic-ui-react";

const AnyReactComponent = ({ text }) => {
    return(
    <div>
        <Icon loading name='heart' size="big"/>
    </div>
)
}

const DEFAULT_POSITION = {
    center: {
        lat: 59.95,
        lng: 30.33
    },
    zoom: 11
}
class SimpleMap extends Component {
    constructor(props){
        super();
        this.state = {...DEFAULT_POSITION};
        this.innerRef = this.props.innerRef
    }

    static getDerivedStateFromProps(nextProps, prevState){
        if(nextProps.innerRef!==prevState.innerRef){
            return { innerRef: nextProps.innerRef};
        }
        else return null;
    }

    componentDidUpdate(prevProps, prevState) {
        if(prevProps.innerRef!==this.props.innerRef){
            //Perform some operation here
            this.setState({someState: someValue});
            this.classMethod();
        }
    }
    render() {

        return (
            <div style={{ height: '100%', width: '100%' }}>
                <GoogleMapReact
                    bootstrapURLKeys={{ key: 'AIzaSyB9cGdWgQvt4wublLDt_ytEGF9TYb128UA' }}
                    defaultCenter={this.props.position.center}
                    defaultZoom={this.props.position.zoom} >
                    <AnyReactComponent
                        lat={this.props.position.lat}
                        lng={this.props.position.lng}
                        text="Your location"
                    />
                </GoogleMapReact>
            </div>
        );
    }
}

export default SimpleMap;
