import React from 'react';
import { List, Image, Button, Transition } from 'semantic-ui-react';
import API from '../../../utils/API';
const moment = require('moment');

const DEFAULT_STATE = {
    notifications: [{}],
};

export default class NotificationsHistory extends React.Component{

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
        this._mounted = false;
    }

    componentDidMount = async() => {
        this._mounted = true;
        await API.getNotifications()
            .then(response => {
                if (response.status === 200)
                    this._mounted && this.setState({notifications: response.data.notifications});
            })
            .catch(e => console.log(e));
    };

    componentWillUnmount() {
        this._mounted = false;
    }

    renderSwitchType(type) {
        switch (type) {
            case 1:
                return 'visited your profile  ';
            case 2:
                return 'send you a message  ';
            case 3:
                return 'liked you back  ';
            case 4:
                return 'remove his like  ';
        }
    }
    render(){
        return (
            <div>
                <Transition.Group
                    as={List}
                    duration={200}
                    divided
                    size='huge'
                    verticalAlign='middle'>
                    {this.state.notifications.map((obj, i) => (
                        <List.Item key={i}>
                            <List.Content>
                                <List.Content floated='right'>
                                    <Button>Dismiss</Button>
                                </List.Content>
                                <Image avatar src={obj.img_link}/>
                                <List.Content as='a'>{obj.firstname}</List.Content>
                                <List.Description>
                                    {this.renderSwitchType(obj.type)}
                                    on {moment(obj.date).fromNow()}
                                </List.Description>
                            </List.Content>
                        </List.Item>
                    ))}
                </Transition.Group>
            </div>
        )
    }
}
