import React from 'react';
import {Form, TextArea, Grid, Progress, Icon, Select, Loader, Dimmer} from 'semantic-ui-react';
import API from "../../../utils/API";
import classnames from 'classnames';
import LocationComponent from "./LocationComponent/LocationComponent";


const DEFAULT_STATE = {
    loading: false,
    complete: 0,
};
class Location extends React.Component {

    constructor(props) {
        super(props);
        this.state = {...DEFAULT_STATE};
        this.state.complete = this.props.complete;
        this.getInnerRef = this.getInnerRef.bind(this);
        this.getLocation = this.getLocation.bind(this);
    }

    componentWillUnmount() {
        this._mounted = false;
    }

    static getDerivedStateFromProps(nextProps, prevState){
        if(nextProps.complete !== prevState.complete)
            return { complete: nextProps.complete}
        return null;
    }


    async componentDidMount() {
        this._mounted = true;
        this.setState({loading: true});
        await this.props.getcomplete();
        this.setState({complete: this.props.complete});
        this.setState({loading: false});
    }

    innerRef;
    getInnerRef(ref) {
        this.innerRef = ref;
    }

    getLocation() {
        this.innerRef && this.innerRef.getLocation();
    }


    render() {
        const {complete} = this.state;
        const { getInnerRef, getLocation } = this;
        return (
            <div className="container-fluid">
                <div className={classnames("ui middle", "Location")}>
                    {/*<LocationComponent ref={getInnerRef}/>*/}
                    <Dimmer active={this.state.loading}>
                        <Loader size='massive'>Get profile...</Loader>
                    </Dimmer>
                    <Grid textAlign="centered">
                        <Grid.Row centered>
                            <h1 className="LastStepTitle">Last step !</h1>
                        </Grid.Row>
                        <Grid.Row centered>
                            <h1 className="NeedLocationTitle">We need your location</h1>
                        </Grid.Row>
                    </Grid>
                    <Grid>
                        <button onClick={getLocation}>Get location</button>
                    </Grid>
                </div>
            </div>
        )
    }
}
export default Location;
