import React, { useState } from 'react';
// import './CardsBuilder.css';
import BottomCard from './../../../components/BottomCard/BottomCard';
import InfoConnect from "./../../../components/UI/InfoConnect/InfoConnect";
import Modal from './../../../components/UI/Modal/Modal';
import Hashtags from './../../../components/BottomCard/Hashtags/Hashtags';
import SignalUserConfirmation from "./../../../components/UI/SignalUser/SignalUserConfirmation/SignalUserConfirmation";
import TopCard from "./../../../components/TopCard/TopCard";
import equal from "fast-deep-equal";
import API from '../../../utils/API';
const DEFAULT_STATE = {
    user_id: null,
    imgs: [],
    firstname: '',
    bio: '',
    gender: '',
    infoConnect: '',
    infoConnectStatus: '',
    interests: [],
    currentImg: 1,
    showHashTags: false,
    showSignalUserModal: false,
    warnings: [],
};

class CardUser extends React.Component {

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
    }

    async componentDidUpdate(prevProps) {
        if(!equal(this.props.userIdFocus, prevProps.userIdFocus)) {
            console.log(1);
            this.setState({user_id: this.props.userIdFocus});
            await API.getUserIdProfile(this.props.userIdFocus)
                .then(res => {
                    if (res.status === 200) {
                        this.setState({
                            imgs: res.data.user.imgs,
                            bio: res.data.user.bio,
                            firstname: res.data.user.firstname,
                            gender: res.data.user.gender,
                        })
                    }
                    else
                        throw new Error("error")
                })
                .catch(err => {
                    console.log(err);
                })
        }
    }

    nextImage = () => {
        if (this.state.currentImg + 1 < this.state.src.length)
            this.setState({currentImg: this.state.currentImg + 1});
    };
    prevImage = () => {
        if (this.state.currentImg > 0)
            this.setState({currentImg: this.state.currentImg - 1});
    };
    showHashTags = () => this.setState({showHashTags: true});
    closeHashTags = () => this.setState({showHashTags: false});
    showSignalUserModal = () => this.setState({showSignalUserModal: true});
    closeSignalUserModal = () => this.setState({showSignalUserModal: false});

    render() {
        let modalHashtags;
        let modalSignalUser;
        const {
            bio,
            currentImg,
            imgs,
            infoConnect,
            infoConnectStatus,
            showHashtags,
            showSignalUserModal,
            user_id,
            firstname,
            gender,
            interests
        } = this.state;

        if (this.state.showHashTags){
            modalHashtags = (
                <Modal
                    show={showHashtags}
                    modalClosed={this.closeHashTags}>
                    <div className="Interests"><h1 className="h1Text">Interests of {firstname}</h1></div>
                    <Hashtags
                        gender={gender}
                        isModalHashtags="true"
                        hashtags={interests}/>
                </Modal>
            )
        }
        if (showSignalUserModal){
            modalSignalUser = (
                <Modal
                    show={showSignalUserModal}
                    modalClosed={this.closeSignalUserModal}>
                    <SignalUserConfirmation
                        name={firstname}
                        SignalUserNo={this.closeSignalUserModal}
                        SignalUserYes={this.closeSignalUserModal}/>
                </Modal>
            )
        }
        if (user_id) {
            return (
                <div className="Card">
                    {modalHashtags}
                    {modalSignalUser}
                    <InfoConnect
                        infoConnectStatus={infoConnectStatus}
                        infoConnect={infoConnect}/>
                    <div className="CardsBuilder">
                        <TopCard
                            src={imgs[currentImg]}
                            name={firstname}
                            nbrImg={imgs}
                            gender={gender}
                            nextImage={this.nextImage}
                            prevImage={this.prevImage}
                            currentImg={currentImg}
                            showSignalUserModal={this.showSignalUserModal}/>
                        <BottomCard
                            bio={bio}
                            hashtags={interests}
                            gender={gender}
                            showHashTags={this.showHashTags}/>
                    </div>
                </div>
            );
        }
        else
            return null;
    }
}

export default CardUser;
