import React from 'react';
import { Grid, Image, Icon } from 'semantic-ui-react';
import equal from 'fast-deep-equal';

const DEFAULT_STATE = {
    users: []
};
class UserMiniCard extends React.Component {

    constructor(props){
        super(props);
        this.state = {...DEFAULT_STATE};
    }

    componentDidUpdate(prevProps) {
        if(!equal(this.props.users, prevProps.users)) {
            this.setState({users: this.props.users});
        }
    }
    render() {
        const { users } = this.state;
            if (users && users.length) {
                return users.map((user, i) => {
                    const {img_link, firstname, latitude} = user;
                    return (
                        <div className="CardContainer">
                            <div className="TopCardContainer">
                                <Image
                                    className="ImageCard"
                                    centered
                                    src={img_link}
                                    key={i}
                                />
                            </div>
                                <Grid
                                    columns={2}
                                    verticalAlign='middle'
                                    centered
                                    className="BottomCardGrid">
                                    <Grid.Column floated='left'>
                                        <Grid.Row>
                                            <span className="LastnameUserMinCard">{firstname.trim()}</span><br />
                                            <span className="DistanceUserMinCard">{latitude.trim()}</span>
                                        </Grid.Row>
                                    </Grid.Column>
                                    <Grid.Column  textAlign='right' floated='right' >
                                        <Grid.Row>
                                            <Icon
                                                circular
                                                name='add'
                                                color='white'
                                                className='SeeIcon'
                                            />
                                        </Grid.Row>
                                    </Grid.Column>
                                </Grid>
                            </div>
                    )
                });
            }
            return null;
    }
}
export default UserMiniCard;
