import {Divider, Grid, Image, Label, Modal, Segment, Icon} from "semantic-ui-react";
import Button from "semantic-ui-react/dist/commonjs/elements/Button";
import React from "react";
import classnames from 'classnames';
import openSocket from 'socket.io-client';
import ModalUserActions from "./ModalUserActions";
const  socket = openSocket('http://localhost:3002');

const DEFAULT_STATE = {
    openHide: false,
    openSignal: false,
    openFake: false
}

class ModalUser extends React.Component {

    state = {...DEFAULT_STATE};

    userLike = async() => {
        socket.emit("like", this.props.user.user_id);
};
    close = () => {

    }
    render() {
        const {user, userInterests } = this.props;
        return (
            <Modal
                dimmer={"blurring"}
                size={'tiny'}
                open={this.props.showModal}
                onClose={this.props.handleClose}
                className="ModalCard">
                <Modal.Header className="CardHeader"><h1
                    className="CardHeaderTile"> {user.firstname} {user.lastname}, <strong>{user.age}</strong></h1>
                </Modal.Header>
                <Modal.Content className="ModalProfilView">
                    <Modal.Header></Modal.Header>
                    <Divider hidden/>
                    <Grid centered columns={2}>
                        <Grid.Column className="RowImages">
                            <Image rounded size='medium' src={user.imgs[0]}/>
                        </Grid.Column>
                        <Grid.Row className="RowImages" centered columns={4}>
                            {user.imgs.map((img, i) => {
                                if (i > 0)
                                    return (
                                        <Grid.Column key={i}>
                                            <Image rounded size='small' src={user.imgs[i]}/>
                                        </Grid.Column>
                                    )
                                return null;
                            })}
                        </Grid.Row>
                    </Grid>
                    <Modal.Description>
                        <Segment className="BioSegment" textAlign="center" inverted>
                            <p>{user.bio}</p>
                        </Segment>
                        <Segment basic textAlign={'center'}>
                            <ModalUserActions
                                openHide={this.state.openHide}
                            />
                            <Button.Group>
                                <Button
                                    size='small'
                                    id='like'
                                    onClick={this.userLike}>
                                <Icon name='heart'/>
                                    Like
                                </Button>
                                <Button
                                    size='small'
                                    id='hide'>
                                    onClick={this.openHide}
                                    <Icon name='user x'/>
                                    Hide
                                </Button>
                                <Button size='small' id='fake'><Icon name='user secret'/>Fake</Button>
                                <Button size='small' id='blockuser'><Icon name='warning'/>Signal</Button>
                            </Button.Group>
                        </Segment>
                        <Divider hidden/>
                        <Label.Group size='small'>
                            <p/>
                            {user.interests.map((data, i) => {
                                return (
                                    <Label
                                        className={userInterests.find(interest => interest === data) ? classnames("interestLabel","sameInterest") : "interestLabel"}
                                        id={i}
                                        key={i}
                                        data={data}>
                                        <strong>#</strong>{data}</Label>
                                )
                            })}
                        </Label.Group>
                    </Modal.Description>
                </Modal.Content>
            </Modal>
        )
    }
}

export default ModalUser;
