import React from 'react';
import {Modal, Input, Divider, Icon } from 'semantic-ui-react'
const moment = require('moment');

class ModalChat extends React.Component {
    constructor(props){
        super(props);
        this._mounted = false;
        this.state = {
            showModal: false,
            user: []
        }
    }

    componentDidMount() {
        this._mounted = true;
    }

    // Outside Ref
    handleOpen = (user) => {this._mounted && this.setState({showModal: true, user: user})};
    handleClose = () => {this._mounted && this.setState({showModal: false, user: []})};

    render(){
        const { showModal, user } = this.state;
        function userOnline() {
            if (user.online)
                return <span className={'OnlineChat'}> <Icon name={'circle'}/> </span>;
            else
                return <span className={'LastConnectionChat'}> <Icon name={'circle'}/> </span>
        };
        return (
            user ?
                <Modal
                    className={'ModalChat'}
                    size={'tiny'}
                    open={showModal}
                    blurring
                    onClose={this.handleClose}
                    closeIcon>
                    <Modal.Header className={'HeaderChat'}>
                        {userOnline()} {' '}
                        <span className={'chatUserBadge'}>{user.firstname}</span>
                    </Modal.Header>
                    <Divider hidden />
                    <Modal.Content scrolling className={'ModalChatContent'}>
                        <div className={'inputChat'}>
                            <Input size='large' action={{ icon: 'send' }} fluid  placeholder='Type a message...' />
                        </div>
                    </Modal.Content>
                </Modal> : null
        )
    }
}

export default ModalChat;
